import {Component, Input} from '@angular/core';

@Component({
  selector: 'app-opening-crawl',
  templateUrl: './opening-crawl.component.html',
  styleUrls: ['./opening-crawl.component.css']
})
export class OpeningCrawlComponent {

  @Input() crawl = "";

  lines: string[] = []

  convert() {
    this.crawl.split("\n")
  }

}
