import {Component, Input, OnInit} from '@angular/core';

@Component({
  selector: 'app-opening-crawl',
  templateUrl: './opening-crawl.component.html',
  styleUrls: ['./opening-crawl.component.css']
})
export class OpeningCrawlComponent implements OnInit{

  @Input() crawl = "";
  @Input() episode = 0;
  @Input() title = "";

  lines: string[] = []

  ngOnInit(): void {
    for (let string of this.crawl.split(".")) {

      string += "."
      console.log(string)
      string.replace("\r\n", "")
      console.log(string)

      this.lines.push(string);
    }
  }

}
